CREATE TABLE Институт
	(
	Код_института int IDENTITY PRIMARY KEY, 
	Название_института varchar(100)
	);

CREATE TABLE Учёная_степень
	(
	Код_ученой_степени int IDENTITY PRIMARY KEY, 
	Название_ученой_степени varchar(100)
	);

CREATE TABLE Группа
	(
	Код_группы int IDENTITY PRIMARY KEY, 
	Номер_группы varchar(100), 
	Курс int, 
	Код_института int FOREIGN KEY REFERENCES Институт(Код_института), 
	Код_ученой_степени int FOREIGN KEY REFERENCES Учёная_степень(Код_ученой_степени)
	);

CREATE TABLE Студент
	(
	Код_студента int IDENTITY PRIMARY KEY, 
	Фамилия_студента varchar(100), 
	Имя_студента varchar(100), 
	Отчество_студента varchar(100), 
	День_рождение_студента date, 
	Телефон_студента varchar(100), 
	Почта_студента varchar(100), 
	Статус_студента bit, 
	Код_группы int FOREIGN KEY REFERENCES Группа(Код_группы)
	);

CREATE TABLE Кафедра
	(
	Код_кафедры int IDENTITY PRIMARY KEY, 
	Название_кафедры varchar(100)
	);

CREATE TABLE Предмет
	(
	Код_предмета int IDENTITY PRIMARY KEY, 
	Название_предмета varchar(100)
	);

CREATE TABLE Преподаватель
	(
	Код_преподавателя int IDENTITY PRIMARY KEY, 
	Фамилия_преподавателя varchar(100), 
	Имя_преподавателя varchar(100), 
	Отчество_преподавателя varchar(100), 
	Стаж int, 
	День_рождение_преподавателя date, 
	Телефон_преподавателя varchar(100), 
	Почта_преподавателя varchar(100), 
	Код_кафедры int FOREIGN KEY REFERENCES Кафедра(Код_кафедры)
	);

CREATE TABLE Ведомость
	(
	Код_ведомости int IDENTITY PRIMARY KEY, 
	Лектор bit, 
	Семинарист bit, 
	Часы int, 
	Месяцы int, 
	Код_предмета int FOREIGN KEY REFERENCES Предмет(Код_предмета), 
	Код_преподавателя int FOREIGN KEY REFERENCES Преподаватель(Код_преподавателя), 
	Код_группы int FOREIGN KEY REFERENCES Группа(Код_группы)
	);

CREATE TABLE Запись_в_ведомость
	(
	Код_ведомости int FOREIGN KEY REFERENCES Ведомость(Код_ведомости), 
	Код_студента int FOREIGN KEY REFERENCES Студент(Код_студента),
	PRIMARY KEY(Код_ведомости, Код_студента), 
	Дата_записи date, 
	Присутствие bit, 
	Активность int, 
	Тестирование int, 
	Экзамен int
	);

