--4.3


--1 Задание
Select 
	Институт.Название_института as 'Название института',
	Группа.Номер_группы as 'Номер группы',
	Учёная_степень.Название_ученой_степени as 'Название учёной степени',
	Concat(Студент.Фамилия_студента, ' ', Студент.Имя_студента, ' ', Студент.Отчество_студента) as 'ФИО студента'
From Институт, Группа, Студент, Учёная_степень
Where
	Институт.Код_института = Группа.Код_института
	and
	Студент.Код_группы = Группа.Код_группы 
	and
	Группа.Код_ученой_степени = Учёная_степень.Код_ученой_степени
Order by 'ФИО студента';



--2 Задание
Select
	Concat(Студент.Фамилия_студента, ' ', Студент.Имя_студента, ' ', Студент.Отчество_студента) as 'ФИО студента',
	Институт.Название_института as 'Название института',
	Группа.Номер_группы as 'Номер группы',
	Учёная_степень.Название_ученой_степени as 'Степень получаемого образования',
	Case When Студент.Статус_студента = 1 Then 'Староста' Else 'Студент' end as Status
From Институт, Группа, Студент, Учёная_степень
Where
	Concat(Студент.Фамилия_студента, ' ', Студент.Имя_студента, ' ', Студент.Отчество_студента) LIKE '[А-О]%' and
	Институт.Код_института = Группа.Код_института and
	Студент.Код_группы = Группа.Код_группы and
	Учёная_степень.Код_ученой_степени between 1 and 2 and
	Группа.Код_ученой_степени = Учёная_степень.Код_ученой_степени 
Order by
	Институт.Название_института asc,
	Учёная_степень.Код_ученой_степени asc,
	Группа.Номер_группы asc,
	'ФИО студента' asc;



--4.4


--1 Задание
Select
	Concat(Студент.Фамилия_студента, ' ', Студент.Имя_студента, ' ', Студент.Отчество_студента) as 'ФИО студента',
	Институт.Название_института as 'Название института',
	Группа.Номер_группы as 'Номер группы',
	Учёная_степень.Название_ученой_степени as 'Степень получаемого образования',
	case when Студент.Статус_студента = 1 then 'Староста' else 'Студент' end as Status
From Группа
	inner join Студент on Студент.Код_группы = Группа.Код_группы
	inner join Учёная_степень on Группа.Код_ученой_степени = Учёная_степень.Код_ученой_степени
	inner join Институт on Институт.Код_института = Группа.Код_института
Where
	Учёная_степень.Код_ученой_степени Between 1 and 2
Order by
	Институт.Название_института asc,
	Учёная_степень.Код_ученой_степени asc,
	Группа.Номер_группы asc,
	'ФИО студента' asc;



--2 Задание
