--3 Задание
Select
	Студент.Фамилия_студента as 'Фамилия студента', 
	sum(Запись_в_ведомость.Экзамен) as 'Сумма баллов за экзамены'
From Студент 
	left join Запись_в_ведомость on Запись_в_ведомость.Код_студента = Студент.Код_студента
Group by Студент.Фамилия_студента
Having sum(Запись_в_ведомость.Экзамен) > 0
Order by Студент.Фамилия_студента asc


--4 Задание
select
	Студент.Фамилия_студента as 'Фамилия студента', 
	avg(Запись_в_ведомость.Экзамен) as 'Сумма баллов за экзамены'
from Студент 
	left join Запись_в_ведомость ON Запись_в_ведомость.Код_студента = Студент.Код_студента
